//const { OMS_SERVER } = process.env;
const fetch = require("node-fetch");
const aut = require("./Authentication");
const ServerRoot = require("@edwinspire/express-server/GetServerRoot");
module.exports = async function SendEvent(EventData) {

   try {
    let responseEvent = await fetch(
        ServerRoot() + "/pgapi/events/receiver",
        {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: aut(),
            },
            body: JSON.stringify(EventData),
        }
    );

    return await responseEvent.json();
   } catch (error) {
       console.error(error);
       return error;
   }

};
